import './app.css';
import { useState } from 'react';
import Axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {
  const [Canal, setCanal] = useState('');
  const [Frecuencia, setFrecuencia] = useState('');
  const [Modo, setModo] = useState('');
  const [Uso, setUso] = useState('');

  const [canalesLista, setLista] = useState([]);

  //1
  const add = () => {
    Axios.post('http://localhost:3001/create',{
      canal:Canal,
      frecuencia:Frecuencia,
      modo:Modo,
      uso:Uso
    }).then(()=>{
      alert('Canal Agregado!');
    });
  }

  //2
  const getCanales = () => {
    Axios.get('http://localhost:3001/canales').then((response)=>{
      setLista(response.data);
    });
  }

  getCanales();

  return (
    <div class="container">
    <div className="App">
      <div classname="datos">


      </div>
        <div className='Lista'>

        {
          canalesLista.map((val,key)=> {
            return <div className=''> {val.canal} </div>
          })
        }

        </div>

    </div>
    /* 1 */
      <div className="card text-center">
        <div className="card-header">
          CANALES ARMADA
        </div>
        <div className="card-body">
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Canal:</span>
            <input type="text"
            onChange={(event)=>{
              setCanal(event.target.value)
            }}
            className="form-control" placeholder="Nombre del Canal" aria-label="Nombre del Canal" aria-describedby="basic-addon1"/>
          </div>
  /* 2 */
  <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Canal:</span>
            <input type="text"
            onChange={(event)=>{
              setCanal(event.target.value)
            }}
            className="form-control" placeholder="Nombre del Canal" aria-label="Nombre del Canal" aria-describedby="basic-addon1"/>
          </div>
          /* 3 */
        <label>Frecuencia: <input 
        onChange={(event)=>{
          setFrecuencia(event.target.value)
        }}
        type='text'/></label>
        <label>Modo: <input 
        onChange={(event)=>{
          setModo(event.target.value)
        }}
        type='text'/></label>
        <label>Uso: <input 
        onChange={(event)=>{
          setUso(event.target.value)
        }}
        type='text'/>  </label>
        </div>
        <div class="card-footer text-muted">
        <button className='btn btn-success' onClick={add}>Registrar</button>
        </div>
      </div>

    </div>
  );
}

export default App;
