import './app.css';
import { useState } from 'react';
import Axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {
  const [Canal, setCanal] = useState('');
  const [Frecuencia, setFrecuencia] = useState('');
  const [Modo, setModo] = useState('');
  const [Uso, setUso] = useState('');

  const [canalesLista, setLista] = useState([]);

  //1
  const add = () => {
    Axios.post('http://localhost:3001/create',{
      canal:Canal,
      frecuencia:Frecuencia,
      modo:Modo,
      uso:Uso
    }).then(()=>{
      alert('Canal Agregado!');
    });
  }

  //2
  const getCanales = () => {
    Axios.get('http://localhost:3001/canales').then((response)=>{
      setLista(response.data);
    });
  }

  getCanales();

  return (
    <div className="App">
      <div classname="datos">
        <label>Canal: <input 
      onChange={(event)=>{
        setCanal(event.target.value)
      }}
        type='text'/></label>
        <label>Frecuencia: <input 
        onChange={(event)=>{
          setFrecuencia(event.target.value)
        }}
        type='text'/></label>
        <label>Modo: <input 
        onChange={(event)=>{
          setModo(event.target.value)
        }}
        type='text'/></label>
        <label>Uso: <input 
        onChange={(event)=>{
          setUso(event.target.value)
        }}
        type='text'/>  </label>      
        <button className='btn btn-success' onClick={add}>Registrar</button>
      </div>
        <div className='Lista'>

        {
          canalesLista.map((val,key)=> {
            return <div className=''> {val.canal} </div>
          })
        }

        </div>

    </div>
  );
}

export default App;
